section.script:
count = 1;
var totalItems = document.getElementsByClassName('carousel-item').length;
window.onload = document.getElementById('question_count_work').innerHTML = `Question ${count} of ${totalItems}`;
console.log("totalItems" + totalItems)
if( count == 1)
{
document.getElementById('ecom_quiz_back_btn').disabled = true;
document.getElementById('ecom_quiz_back_btn').style.opacity = "0";
document.getElementById('ecom_quiz_back_btn').style.cursor = "default";
}
function next()
{
count++;
document.getElementById('question_count_work').innerHTML = `Question ${count} of ${totalItems}`;
if( count > 1)
{
document.getElementById('ecom_quiz_back_btn').disabled = false;
document.getElementById('ecom_quiz_back_btn').style.opacity = "1";
document.getElementById('ecom_quiz_back_btn').style.cursor = "pointer";
}
if( count == totalItems)
{
document.getElementById('ecom_quiz_next_btn').style.display = "none";
document.getElementById('ecom_quiz_submit').style.display = "block";
return totalItems;
}
};
function prev(){
if(count > 1)
{
count--;
document.getElementById('question_count_work').innerHTML = `Question ${count} of ${totalItems}`;

}
if( count == 1 )
{
document.getElementById('question_count_work').innerHTML = `Question 1 of ${totalItems}`;
document.getElementById('ecom_quiz_back_btn').style.opacity = "0";
document.getElementById('ecom_quiz_back_btn').style.cursor = "default";

}
if(count != totalItems){
document.getElementById('ecom_quiz_next_btn').style.display = "block";
document.getElementById('ecom_quiz_submit').style.display = "none";
}
};
var value_input = document.getElementsByClassName('form_control');
console.log("quiz_length =" + value_input.length);
var url = window.location.href;
var user_inputs = [];

var submit_button = document.getElementById('ecom_quiz_submit');
console.log('ss',value_input)
document.addEventListener('input', function(){
for (var radio of value_input)
{
if(radio.checked) {
console.log("Radio_Value =" + radio.value);
console.log("Radio_details =" + radio.name);
let submit_value = "InputValue="+radio.value + "/URL="+ url + "/QuestionId=" + radio.name
console.log("submit_value =" + submit_value);
user_inputs.push(submit_value)

submit_button.href = "/submit/quizzes/?input_value=" + user_inputs;
}
}
});

