   function accept_btn() {
       var element = document.getElementById('checbox_errror_msg');
   if ( document.getElementById('terms_new_condition').checked ) {
       $("[data-dismiss=modal]").trigger({ type: "click" });
        document.getElementById("terms_new_condition").checked = "ture";
        element.style.opacity = "0";
   }
   else {
       element.style.opacity = "1";
       }
   }
function formFunction_closebtn() {
   var elementgo = document.getElementById('checbox_errror_msg');
   document.getElementById("terms_new_condition").checked = false;
   elementgo.style.opacity = "0";
}

function formsignup_new() {
   var elmc = document.getElementById('terms_new_condition');
   if (elmc.checked == false){
       alert("Check the checkbox to continue");
   }
}
